mapper.select:
SELECT `id`, `name`, `description`, `granted_authority`, `create_time`, `update_time` FROM `role` WHERE `id` IN ( SELECT `role_id` FROM rel_employee_role WHERE `emp_id` = #{userId}
UNION
SELECT `role_id` FROM `rel_employee_group_role` WHERE `group_id` IN ( SELECT `group_id` FROM `rel_employee_group` WHERE `emp_id` = #{userId} ) )
